mapper.select:
SELECT COUNT(*) AS totalRow FROM sp_pp_resultlist JOIN sp_pp ON sp_pp.pp_number = sp_pp_resultlist.pp_number JOIN sp_pp_name_cate ON sp_pp_name_cate.pp_name_number = sp_pp.pp_name_number JOIN sp_farm ON sp_farm.f_number = sp_pp_resultlist.f_number JOIN sp_farm_member ON sp_farm_member.f_member_id = sp_pp_resultlist.f_member_id WHERE sp_pp_resultlist.pp_resultlist_number = sp_pp_resultlist.pp_resultlist_number
AND sp_pp_resultlist.f_number = #{fNumber}
AND sp_pp_name_cate.pp_name_planname LIKE CONCAT('%',#{searchKeyword},'%')
AND sp_pp_resultlist.pp_resultlist_date BETWEEN #{startDate} AND #{endDate}
AND sp_farm.f_name LIKE CONCAT('%',#{searchKeyword},'%')
AND sp_farm_member.f_member_id LIKE CONCAT('%',#{searchKeyword},'%')

SELECT sp_pp_name_cate.pp_name_planname, sp_pp_resultlist.pp_resultlist_number, sp_pp_resultlist.f_number, sp_pp_resultlist.pp_resultlist_date, sp_farm.f_name, sp_farm_member.f_member_id, sp_farm_member.f_member_name FROM sp_pp_resultlist JOIN sp_pp ON sp_pp.pp_number = sp_pp_resultlist.pp_number JOIN sp_pp_name_cate ON sp_pp_name_cate.pp_name_number = sp_pp.pp_name_number JOIN sp_farm ON sp_farm.f_number = sp_pp_resultlist.f_number JOIN sp_farm_member ON sp_farm_member.f_member_id = sp_pp_resultlist.f_member_id WHERE sp_pp_resultlist.pp_resultlist_number = sp_pp_resultlist.pp_resultlist_number
AND sp_pp_resultlist.f_number = #{fNumber}
AND sp_pp_name_cate.pp_name_planname LIKE CONCAT('%',#{searchKeyword},'%')
AND sp_pp_resultlist.pp_resultlist_date BETWEEN #{startDate} AND #{endDate}
AND sp_farm.f_name LIKE CONCAT('%',#{searchKeyword},'%')
AND sp_farm_member.f_member_id LIKE CONCAT('%',#{searchKeyword},'%')
ORDER BY sp_pp_resultlist.pp_resultlist_number DESC LIMIT #{beginRow}, #{pagePerRow}
