mapper.update:
update sp_deal_company
set
	c_name=#{cName}
	,c_member_id=#{cMemberId}
	,c_doroaddress=#{cDoroaddress}
	,c_jibunaddress=#{cJibunaddress}
	,c_phone=#{cPhone}
	,c_party=#{cParty}
where c_number=#{cNumber}

mapper.insert:

	SELECT LAST_INSERT_ID()

insert into sp_deal_company
	(deal_number
	,c_name
	,c_member_id
	,c_doroaddress
	,c_jibunaddress
	,c_phone
	,c_registdate
	,c_party)
values
	(#{dealNumber}
	,#{cName}
	,#{cMemberId}
	,#{cDoroaddress}
	,#{cJibunaddress}
	,#{cPhone}
	,NOW()
	,#{cParty})
