mapper.insert:
 		
			SELECT LAST_INSERT_ID()
		
 			INSERT INTO sp_pp_work(
				pp_number
				, f_number
				, pp_work_name
				, pp_work_startday
				, pp_work_endday
				, pp_work_area
				, pp_work_content)
			VALUES (
				#{productionPlan.ppNumber}
				, #{farm.fNumber}
				, #{ppWorkName}
				, #{ppWorkStartday}
				, #{ppWorkEndday}
				, #{ppWorkArea}
				, #{ppWorkContent})

mapper.select:
SELECT sp_pp_work.pp_work_number , sp_pp_work.pp_work_name , sp_pp_work.pp_work_startday , sp_pp_work.pp_work_endday , sp_pp_work.pp_work_content , sp_pp_work.pp_work_area , sp_pp.pp_number , sp_pp.pp_ownarea , sp_pp.pp_rentarea , sp_farm.f_number , sp_farm.f_name FROM sp_pp_work JOIN sp_pp ON sp_pp_work.pp_number = sp_pp.pp_number JOIN sp_farm ON sp_pp_work.f_number = sp_farm.f_number WHERE sp_pp.pp_number=#{ppNumber}
AND sp_pp_work.pp_work_name LIKE CONCAT('%',#{searchKeyword},'%')
AND sp_pp_work.pp_work_content LIKE CONCAT('%',#{searchKeyword},'%')
