mapper.update:
 	UPDATE sp_e_insurancepay
	SET
		sp_e_insurancepay.theme_number = #{categoryTheme.themeNumber}
		,sp_e_insurancepay.pp_work_number = #{ppWork.ppWorkNumber}
		,sp_e_insurancepay.e_insurancepay_name = #{eInsurancepayMame}
		,sp_e_insurancepay.e_insurancepay_content = #{eInsurancepayContent}
		,sp_e_insurancepay.e_insurancepay_startday = #{eInsurancepayStartday}
		,sp_e_insurancepay.e_insurancepay_endday = #{eInsurancepayEndday}
		,sp_e_insurancepay.e_insurancepay_term = #{eInsurancepayTerm}
		,sp_e_insurancepay.e_insurancepay_totalcost = #{eInsurancepayTotalcost}
		,sp_e_insurancepay.e_insurancepay_expectcost = #{eInsurancepayExpectcost}
		,sp_e_insurancepay.e_insurancepay_secret = #{eInsurancepaySecret}
	WHERE
		sp_e_insurancepay.e_insurancepay_number = #{eInsurancepayNumber}

mapper.select:
select sp_e_insurancepay.e_insurancepay_number ,sp_e_insurancepay.theme_number ,sp_theme_cate.theme_name ,sp_pp.pp_number ,sp_e_insurancepay.pp_work_number ,sp_pp_work.pp_work_name ,sp_e_insurancepay.e_insurancepay_name ,sp_e_insurancepay.e_insurancepay_content ,sp_e_insurancepay.e_insurancepay_startday ,sp_e_insurancepay.e_insurancepay_endday ,sp_e_insurancepay.e_insurancepay_term ,sp_e_insurancepay.e_insurancepay_totalcost ,sp_e_insurancepay.e_insurancepay_expectcost ,sp_e_insurancepay.e_insurancepay_secret from sp_e_insurancepay join sp_theme_cate join sp_pp_work join sp_pp on sp_e_insurancepay.theme_number = sp_theme_cate.theme_number and sp_e_insurancepay.pp_work_number = sp_pp_work.pp_work_number and sp_pp_work.pp_number = sp_pp.pp_number where sp_e_insurancepay.pp_work_number = #{ppWorkNumber}
sp_pp_work.pp_work_name like concat('%',#{property},'%') and sp_e_insurancepay.pp_work_number = #{ppWorkNumber}
sp_theme_cate.theme_name like concat('%',#{property},'%') and sp_e_insurancepay.pp_work_number = #{ppWorkNumber}
sp_e_insurancepay.e_insurancepay_number = #{property}
